    return(
      <li key={this.props.details.index}>
            {this.props.details.nom}
            {/* on ajoute un bouton pout editer les cours onclick*/}
            <button>Edit</button>
            <button onClick={()=>this.props.deleteCourse(this.props.index)}>Delete</button>
            </li>
    )
  }
  toggleState=()=>{
